import { BbsKeyPair } from "./BbsKeyPair";
/**
 * A request to create a BBS signature for a set of messages
 */
export interface BbsSignRequest {
    /**
     * BBS key pair
     */
    readonly keyPair: BbsKeyPair;
    /**
     * Messages to sign
     */
    readonly messages: readonly Uint8Array[];
}
